    setSearchResult(null);
    setApi2Error(null);

    setMainPageDetails((prev) => ({
      ...prev,
      [name]: value,
    }));
    setErrors((prev) => ({ ...prev, [name]: "" }));

    // Fetch availability when doctor is selected
